import React, { useEffect, useState } from "react";
import axios from "axios";

const News = () => {
  const [articles, setArticles] = useState([]);
  const [personalized, setPersonalized] = useState(false);

  const fetchNews = async () => {
    const token = localStorage.getItem("token");
    const endpoint = personalized
      ? "http://localhost:8000/api/v1/news/personalized"
      : "http://localhost:8000/api/v1/news/general";

    try {
      const res = await axios.get(endpoint, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArticles(res.data.articles);
    } catch (err) {
      alert("Failed to fetch news");
    }
  };

  useEffect(() => {
    fetchNews();
  }, [personalized]);

  return (
    <div className="news-container">
      <div className="news-header">
        <h2>{personalized ? "Personalized News" : "General News"}</h2>
        <button onClick={() => setPersonalized(!personalized)}>
          {personalized ? "Show General News" : "Show Personalized News"}
        </button>
      </div>
      <div className="news-list">
        {articles.map((article, idx) => (
          <div key={idx} className="news-card">
            <h3>{article.title}</h3>
            <p>{article.description}</p>
            <a href={article.url} target="_blank" rel="noopener noreferrer">
              Read more
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default News;
