import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaBell } from "react-icons/fa";
import { io } from "socket.io-client";

const socket = io("http://localhost:8000"); // Update if needed

const News = () => {
  const [articles, setArticles] = useState([]);
  const [personalized, setPersonalized] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [sentiments, setSentiments] = useState({});
  const [sentimentLoading, setSentimentLoading] = useState({});
  const [notifications, setNotifications] = useState([]);
  const [preferences, setPreferences] = useState([]);
  const [availablePreferences, setAvailablePreferences] = useState([]);
  const [dropdownVisiblePreferences, setDropdownVisiblePreferences] =
    useState(false);
  const [articlesLoaded, setArticlesLoaded] = useState(false);

  // Fetch user preferences from API
  const fetchPreferences = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(
        "http://localhost:8000/api/v1/users/getPreferences",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setPreferences(res.data.data.selectedPreferences);
      setAvailablePreferences(res.data.data.availablePreferences);
    } catch (err) {
      console.error("Error fetching preferences:", err);
    }
  };

  // Fetch news based on preferences
  const fetchNews = async () => {
    const token = localStorage.getItem("token");
    const endpoint = personalized
      ? "http://localhost:8000/api/v1/users/personalizedNews"
      : "http://localhost:8000/api/v1/users/allNews";

    try {
      setLoading(true);
      const res = await axios.get(endpoint, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArticles(res.data.data || []);
      setError("");
      setArticlesLoaded(true);
    } catch (err) {
      console.error("Failed to fetch news:", err.response?.data || err.message);
      setError(
        err.response?.data?.message ||
          err.response?.data?.error ||
          err.message ||
          "Failed to fetch news."
      );
    } finally {
      setLoading(false);
    }
  };

  // Add new preference
  const handleAddPreference = async (preference) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:8000/api/v1/users/updatePreferences",
        {
          preferencesToAdd: [preference],
          preferencesToRemove: [],
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setPreferences(res.data.data);
      setAvailablePreferences((prev) =>
        prev.filter((item) => item !== preference)
      );
    } catch (err) {
      console.error("Error adding preference:", err);
    }
  };

  // Remove preference
  const handleRemovePreference = async (preference) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:8000/api/v1/users/updatePreferences",
        {
          preferencesToAdd: [],
          preferencesToRemove: [preference],
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setPreferences(res.data.data);
      setAvailablePreferences((prev) => [...prev, preference]);
    } catch (err) {
      console.error("Error removing preference:", err);
    }
  };

  // Toggle the personalized news view
  const togglePersonalized = () => {
    setPersonalized(!personalized);
  };

  useEffect(() => {
    fetchPreferences(); // Fetch user preferences when component mounts
    fetchNews(); // Fetch news based on preferences

    socket.on("connect", () => {
      console.log("âœ… Socket connected");
    });

    socket.on("disconnect", () => {
      console.log("âŒ Socket disconnected");
    });

    socket.on("breaking-news", (article) => {
      console.log("ðŸš¨ Received breaking news:", article);

      if (article && article.title) {
        const time = new Date().toLocaleTimeString([], {
          hour: "2-digit",
          minute: "2-digit",
        });
        setNotifications((prev) => [
          {
            ...article,
            receivedAt: time,
          },
          ...prev,
        ]);
      } else {
        console.log("Received invalid article:", article);
      }
    });

    return () => {
      socket.off("breaking-news");
      socket.off("connect");
      socket.off("disconnect");
    };
  }, []);

  return (
    <div className="news-container">
      <div className="news-header">
        <h2>{personalized ? "Personalized News" : "General News"}</h2>
        <button onClick={togglePersonalized} className="toggle-button">
          {personalized ? "Show General News" : "Show Personalized News"}
        </button>
        <button
          onClick={() => setDropdownVisiblePreferences((prev) => !prev)}
          className="change-preferences-button"
        >
          Change Preferences
        </button>
      </div>

      {/* Show selected preferences */}
      {dropdownVisiblePreferences && (
        <div className="preferences-dropdown">
          <div className="selected-preferences">
            <strong>Selected Preferences:</strong>
            <div>
              {preferences.map((pref, idx) => (
                <div key={idx} className="tag">
                  {pref}
                  <span
                    onClick={() => handleRemovePreference(pref)}
                    style={{ cursor: "pointer" }}
                  >
                    {" "}
                    x
                  </span>
                </div>
              ))}
            </div>
          </div>

          <div className="dropdown">
            <div className="dropdown-header">Select Preferences</div>
            <div className="dropdown-options">
              {availablePreferences.map((pref, idx) => (
                <div
                  key={idx}
                  className="dropdown-item"
                  onClick={() => handleAddPreference(pref)}
                  style={{ cursor: "pointer" }}
                >
                  {pref}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Display news */}
      <div className="news-list">
        {!loading && articles.length === 0 && !error && (
          <p>No news available at the moment.</p>
        )}
        {articles.map((article, idx) => (
          <div key={idx} className="news-card">
            <h3>{article.title}</h3>
            {article.urlToImage && (
              <img
                src={article.urlToImage}
                alt="news"
                style={{
                  width: "100%",
                  maxHeight: 200,
                  objectFit: "cover",
                  borderRadius: "8px",
                  marginBottom: "10px",
                }}
              />
            )}
            <p>{article.description}</p>
            <a href={article.url} target="_blank" rel="noopener noreferrer">
              Read more
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default News;
