import React, { useEffect, useState } from "react";
import axios from "axios";

const News = () => {
  const [articles, setArticles] = useState([]);
  const [personalized, setPersonalized] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [sentiments, setSentiments] = useState({});
  const [sentimentLoading, setSentimentLoading] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchNews = async (page = 1) => {
    const token = localStorage.getItem("token");
    const endpoint = personalized
      ? "http://localhost:8000/api/v1/users/personalizedNews"
      : "http://localhost:8000/api/v1/users/allNews";

    try {
      setLoading(true);
      const res = await axios.get(endpoint, {
        params: { page, pageSize: 10 },
        headers: { Authorization: `Bearer ${token}` },
      });

      setArticles(res.data.data || []);
      setTotalPages(Math.ceil(res.data.count / 10));
      setError("");
    } catch (err) {
      console.error("Failed to fetch news:", err.response?.data || err.message);
      const message =
        err.response?.data?.message ||
        err.response?.data?.error ||
        err.message ||
        "Failed to fetch news.";
      setError(message);
    } finally {
      setLoading(false);
    }
  };

  const fetchSentiment = async (text, index) => {
    try {
      setSentimentLoading((prev) => ({ ...prev, [index]: true }));
      const res = await axios.post("http://localhost:8000/api/v1/analyze", {
        text,
      });

      setSentiments((prev) => ({ ...prev, [index]: res.data }));
    } catch (err) {
      console.error("Sentiment fetch failed:", err);
    } finally {
      setSentimentLoading((prev) => ({ ...prev, [index]: false }));
    }
  };

  const handlePagination = (direction) => {
    const newPage = direction === "next" ? currentPage + 1 : currentPage - 1;
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
      fetchNews(newPage);
    }
  };

  useEffect(() => {
    fetchNews(currentPage);
  }, [personalized, currentPage]);

  return (
    <div className="news-container">
      <div className="news-header">
        <h2>{personalized ? "Personalized News" : "General News"}</h2>
        <button onClick={() => setPersonalized(!personalized)}>
          {personalized ? "Show General News" : "Show Personalized News"}
        </button>
      </div>

      {error && <p style={{ color: "red", marginBottom: "10px" }}>{error}</p>}

      {loading && (
        <div className="spinner-container">
          <div className="spinner"></div>
        </div>
      )}

      <div className="news-list">
        {!loading && articles.length === 0 && !error && (
          <p>No news available at the moment.</p>
        )}

        {articles.map((article, idx) => (
          <div key={idx} className="news-card" style={{ marginBottom: "20px" }}>
            <h3>{article.title}</h3>

            {article.urlToImage && (
              <img
                src={article.urlToImage}
                alt="news"
                style={{
                  width: "100%",
                  maxHeight: 200,
                  objectFit: "cover",
                  borderRadius: "8px",
                  marginBottom: "10px",
                }}
              />
            )}

            <p>{article.description}</p>

            {!sentiments[idx] && !sentimentLoading[idx] && (
              <button onClick={() => fetchSentiment(article.description, idx)}>
                Show Sentiment
              </button>
            )}

            {sentimentLoading[idx] && (
              <div className="spinner-container">
                <div className="spinner"></div>
              </div>
            )}

            {sentiments[idx] && (
              <div>
                <strong>Sentiment Analysis:</strong>
                <p>Negative: {(sentiments[idx].negative * 100).toFixed(1)}%</p>
                <p>Neutral: {(sentiments[idx].neutral * 100).toFixed(1)}%</p>
                <p>Positive: {(sentiments[idx].positive * 100).toFixed(1)}%</p>
              </div>
            )}

            <a href={article.url} target="_blank" rel="noopener noreferrer">
              Read more
            </a>
          </div>
        ))}
      </div>

      <div className="pagination-controls">
        <button
          onClick={() => handlePagination("prev")}
          disabled={currentPage === 1}
        >
          Previous
        </button>

        <span>
          Page {currentPage} of {totalPages}
        </span>

        <button
          onClick={() => handlePagination("next")}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default News;
