import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaBell } from "react-icons/fa"; // Import FaBell from react-icons
import { io } from "socket.io-client"; // Import socket.io client

const socket = io("http://localhost:8000"); // or your backend URL

const News = () => {
  const [articles, setArticles] = useState([]);
  const [personalized, setPersonalized] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [sentiments, setSentiments] = useState({});
  const [sentimentLoading, setSentimentLoading] = useState({});
  const [notifications, setNotifications] = useState([]);
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const fetchNews = async () => {
    const token = localStorage.getItem("token");
    const endpoint = personalized
      ? "http://localhost:8000/api/v1/users/personalizedNews"
      : "http://localhost:8000/api/v1/users/allNews";

    try {
      setLoading(true);
      const res = await axios.get(endpoint, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setArticles(res.data.data || []);
      setError("");
    } catch (err) {
      console.error("Failed to fetch news:", err.response?.data || err.message);
      const message =
        err.response?.data?.message ||
        err.response?.data?.error ||
        err.message ||
        "Failed to fetch news.";
      setError(message);
    } finally {
      setLoading(false);
    }
  };

  const fetchSentiment = async (summary, articleId) => {
    setSentimentLoading((prev) => ({ ...prev, [articleId]: true }));
    try {
      const response = await axios.post(
        "http://localhost:8000/api/v1/users/getSentiment",
        { summary }
      );

      setSentiments((prev) => ({
        ...prev,
        [articleId]: {
          negative: response.data.negative || 0,
          neutral: response.data.neutral || 0,
          positive: response.data.positive || 0,
        },
      }));
    } catch (error) {
      console.error("Error fetching sentiment:", error);
    } finally {
      setSentimentLoading((prev) => ({ ...prev, [articleId]: false }));
    }
  };

  useEffect(() => {
    fetchNews();
  }, [personalized]);

  useEffect(() => {
    socket.on("breaking-news", (article) => {
      setNotifications((prev) => [...prev, article]);
    });

    return () => {
      socket.off("breaking-news");
    };
  }, []);

  return (
    <div className="news-container">
      <div className="news-header">
        <h2>{personalized ? "Personalized News" : "General News"}</h2>
        <div style={{ display: "flex", alignItems: "center", gap: "15px" }}>
          <div
            style={{ position: "relative", cursor: "pointer" }}
            onClick={() => setDropdownVisible((prev) => !prev)}
          >
            {/* Use FaBell from react-icons */}
            <FaBell style={{ fontSize: "20px" }} />
            {notifications.length > 0 && (
              <span
                style={{
                  position: "absolute",
                  top: "-5px",
                  right: "-5px",
                  backgroundColor: "red",
                  color: "white",
                  borderRadius: "50%",
                  padding: "2px 6px",
                  fontSize: "12px",
                }}
              >
                {notifications.length}
              </span>
            )}

            {/* Dropdown Menu */}
            {dropdownVisible && (
              <div
                style={{
                  position: "absolute",
                  top: "30px",
                  right: "0",
                  backgroundColor: "white",
                  border: "1px solid #ddd",
                  boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
                  maxHeight: "200px",
                  overflowY: "auto",
                  width: "250px",
                  borderRadius: "8px",
                  padding: "10px",
                  zIndex: 10,
                }}
              >
                {notifications.length > 0 ? (
                  notifications.map((notification, idx) => (
                    <div key={idx} style={{ marginBottom: "10px" }}>
                      <strong>{notification.title}</strong>
                    </div>
                  ))
                ) : (
                  <p>No new notifications</p>
                )}
              </div>
            )}
          </div>
          <button onClick={() => setPersonalized(!personalized)}>
            {personalized ? "Show General News" : "Show Personalized News"}
          </button>
        </div>
      </div>

      {error && <p style={{ color: "red", marginBottom: "10px" }}>{error}</p>}
      {loading && (
        <div className="spinner-container">
          <div className="spinner"></div>
        </div>
      )}

      <div className="news-list">
        {!loading && articles.length === 0 && !error && (
          <p>No news available at the moment.</p>
        )}
        {articles.map((article, idx) => (
          <div key={idx} className="news-card" style={{ marginBottom: "20px" }}>
            <h3>{article.title}</h3>
            {article.urlToImage && (
              <img
                src={article.urlToImage}
                alt="news"
                style={{
                  width: "100%",
                  maxHeight: 200,
                  objectFit: "cover",
                  borderRadius: "8px",
                  marginBottom: "10px",
                }}
              />
            )}
            <p>{article.description}</p>

            {!sentiments[idx] && !sentimentLoading[idx] && (
              <button onClick={() => fetchSentiment(article.description, idx)}>
                Show Sentiment
              </button>
            )}

            {sentimentLoading[idx] && (
              <div className="spinner-container">
                <div className="spinner"></div>
              </div>
            )}
            {sentiments[idx] && (
              <div>
                <strong>Sentiment Analysis:</strong>
                <p>Negative: {(sentiments[idx].negative * 100).toFixed(1)}%</p>
                <p>Neutral: {(sentiments[idx].neutral * 100).toFixed(1)}%</p>
                <p>Positive: {(sentiments[idx].positive * 100).toFixed(1)}%</p>
              </div>
            )}
            <a href={article.url} target="_blank" rel="noopener noreferrer">
              Read more
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

// On the frontend (News.js)
socket.on("test", (message) => {
  console.log(message); // This should log "Hello from the server"
});

export default News;
